$this->load->model('checkout/order');
$order_total = $this->model_checkout_order->getTotalOrders();

//var_dump($order_total);
	if ($order_total > 0) {			
		$this->response->redirect($this->url->link('checkout/cart'));
	}
	}
/* RESTRICTED SHOPPING END */

if (isset($data['like'])){
